function showError(e,r){const t=e.parentElement;return t.classList.add("error"),t.querySelector(".message").innerText=r,!1}function showSuccess(e){const r=e.parentElement;return r.classList.remove("error"),r.querySelector(".message").innerText="",!0}function checkEmail(e){return/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e.value.trim())?showSuccess(e):showError(e,"Email không hợp lệ")}function checkRequired(e){let r=!1;return e.forEach(function(e){""===e.value.trim()?(showError(e,`${getFieldName(e)} không được để trống`),r=!1):(showSuccess(e),r=!0)}),r}function checkLength(e,r,t){return e.value.length<r?showError(e,`${getFieldName(e)} phải có ít nhất ${r} ký tự`):e.value.length>t?showError(e,`${getFieldName(e)} phải ít hơn ${t} ký tự`):showSuccess(e)}function checkPasswordsMatch(e,r){return e.value!==r.value?showError(r,"Mật khẩu không trùng khớp"):showSuccess(r)}function getFieldName(e){return document.querySelector(`label[for='${e.id}']`).innerText}