function showError(e,r){const s=e.parentElement;s.className="form-control error",s.querySelector("small").innerText=r}function showSuccess(e){e.parentElement.className="form-control success"}function checkEmail(e){/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e.value.trim())?showSuccess(e):showError(e,"Email is not valid")}function checkRequired(e){let r=!1;return e.forEach(function(e){""===e.value.trim()?(showError(e,`${getFieldName(e)} is required`),r=!0):showSuccess(e)}),r}function checkLength(e,r,s){e.value.length<r?showError(e,`${getFieldName(e)} must be at least ${r} characters`):e.value.length>s?showError(e,`${getFieldName(e)} must be less than ${s} characters`):showSuccess(e)}function checkPasswordsMatch(e,r){e.value!==r.value&&showError(r,"Passwords do not match")}function getFieldName(e){return e.id.charAt(0).toUpperCase()+e.id.slice(1)}